// Card for displaying a procurement request
import React, { useState } from 'react';
import Button from './Button';
import CustomCheckbox from './CustomCheckbox';
import Badge from './Badge';
import RequestIcon from './RequestIcon';

/**
 * RequestCard for displaying a procurement request
 * @param {string} vendor - Vendor name
 * @param {boolean} isNewVendor - Whether this is a new vendor
 * @param {string} title - Request title/description
 * @param {string} date - Date string
 * @param {string} requester - Requester name
 * @param {string} amount - Amount string
 * @param {string} status - Status label (OPEN/APPROVE/COMPLETED)
 * @param {function} onStatusClick - Click handler for status button
 * @param {boolean} selected - Whether the card is selected
 * @param {function} onSelect - Handler for selection
 * @param {string} badge - Badge text
 * @param {string} badgeColor - Badge color
 * @param {function} onCardClick - Handler for card click
 * @param {string} className - Additional classes
 */
export default function RequestCard({
  vendor,
  isNewVendor = false,
  title,
  date,
  requester,
  amount,
  status,
  onStatusClick,
  selected = false,
  onSelect,
  badge,
  badgeColor = 'green',
  className = '',
  onCardClick,
}) {
  const [hovered, setHovered] = useState(false);
  
  // Only show checkbox for pending approval items
  const showCheckbox = status === 'APPROVE';
  
  return (
    <div
      className={`relative p-6 rounded-xl flex items-center transition-all duration-150 cursor-pointer border-2
        ${selected ? 'border-blue-500 bg-blue-50 shadow-lg' : hovered ? 'border-gray-300 bg-gray-50 shadow-sm' : 'border-gray-200 bg-white'}
        ${className}`}
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
      onClick={e => {
        if (e.target.closest('.checkbox-wrapper')) return;
        if (e.target.closest('.action-button')) return;
        if (onCardClick) onCardClick();
      }}
    >
      {/* Left section: Checkbox and Icon */}
      <div className="flex items-center space-x-4">
        {showCheckbox && (
          <div className="checkbox-wrapper">
            <CustomCheckbox checked={selected} onChange={onSelect} />
          </div>
        )}
        <div className="flex flex-col items-center min-w-[56px]">
          <RequestIcon />
          <span className="text-xs font-medium text-gray-600 mt-1">REQUEST</span>
        </div>
      </div>
      
      {/* Middle section: Request details */}
      <div className="flex-1 ml-6">
        <div className="flex items-center space-x-3 mb-1">
          <span className="font-semibold text-lg text-gray-900">{vendor}</span>
          {(isNewVendor || badge) && (
            <Badge 
              text={badge || 'New vendor'} 
              color={badgeColor} 
            />
          )}
        </div>
        <p className="text-sm text-gray-700 font-medium">{title}</p>
      </div>
      
      {/* Right section: Date, amount, and action button */}
      <div className="flex items-center space-x-8">
        {/* Date and requester info */}
        <div className="text-right">
          <div className="text-xs text-gray-500 font-medium">
            {status === 'OPEN' ? 'Created' : status === 'APPROVE' ? 'Requested' : 'Requested'}
          </div>
          <div className="text-sm text-gray-700 font-medium">{date}</div>
        </div>
        
        {/* Amount and requester */}
        <div className="text-right">
          <div className="font-bold text-lg text-gray-900">{amount}</div>
          <div className="text-xs text-gray-500 font-medium">Requested by {requester}</div>
        </div>
        
        {/* Action button */}
        <div className="action-button">
          <Button
            variant="secondary"
            className={`px-6 py-2 rounded-lg border-2 font-medium transition-colors
              ${status === 'OPEN' 
                ? 'border-gray-400 text-gray-900 bg-white hover:bg-gray-50' 
                : status === 'APPROVE' 
                ? 'border-gray-400 text-gray-900 bg-white hover:bg-gray-50'
                : 'border-green-400 text-green-700 bg-green-50 hover:bg-green-100'
              }`}
            onClick={onStatusClick}
          >
            {status}
          </Button>
        </div>
      </div>
    </div>
  );
}
