// Sidebar navigation for the procurement app
import React, { useState } from 'react';

const navItems = [
  { icon: (
      <svg className="w-6 h-6" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M3 12l2-2m0 0l7-7 7 7M13 5v6h6m-6 0H7m6 0v6m0 0l-2 2m2-2l2 2" /></svg>
    ), key: 'home', label: 'Home' },
  { icon: (
      <svg className="w-6 h-6" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M9 17v-6a2 2 0 012-2h2a2 2 0 012 2v6m-6 0h6" /></svg>
    ), key: 'requests', label: 'Requests' },
  { icon: (
      <svg className="w-6 h-6" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M15 17h6m-3-3v6m-9-3H3m3 0H3m3 0v6m0 0l-2 2m2-2l2 2" /></svg>
    ), key: 'approvals', label: 'Approvals' },
];

/**
 * Sidebar navigation with logo and icons
 * @param {string} active - The active nav item key
 * @param {function} onNavClick - Callback for nav item click
 */
export default function Sidebar({ active = 'home', onNavClick, logo }) {
  const [expanded, setExpanded] = useState(true);
  const [logoError, setLogoError] = useState(false);
  return (
    <aside className={`sidebar ${expanded ? 'w-48' : 'w-16'} transition-all duration-200 flex flex-col items-center py-4 space-y-6 text-white bg-[#2c3e50] h-screen justify-between`}>
      <div className="flex flex-col items-center w-full">
        {/* Hamburger menu and logo */}
        <div className="flex items-center w-full px-2 mb-4">
          <button
            className="mr-2 p-2 focus:outline-none"
            onClick={() => setExpanded(e => !e)}
            aria-label={expanded ? 'Collapse sidebar' : 'Expand sidebar'}
          >
            {/* Hamburger icon */}
            <svg className="w-6 h-6" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M4 6h16M4 12h16M4 18h16" /></svg>
          </button>
          {logo && !logoError ? (
            <img src={logo} alt="Company Logo" className={`h-8 ${expanded ? 'ml-1' : ''}`} onError={() => setLogoError(true)} />
          ) : (
            expanded && <div className="text-2xl font-bold ml-1">t</div>
          )}
        </div>
        <nav className="flex flex-col items-center space-y-2 flex-grow mt-4 w-full">
          {navItems.map(item => (
            <button
              key={item.key}
              className={`flex items-center w-full p-3 rounded-lg nav-item relative group transition-colors duration-150 ${active === item.key ? 'bg-[#34495e]' : ''}`}
              onClick={() => onNavClick && onNavClick(item.key)}
              aria-label={item.label}
            >
              <span className="flex-shrink-0">{item.icon}</span>
              {expanded && (
                <span className="ml-3 text-base font-medium whitespace-nowrap">{item.label}</span>
              )}
              {/* Tooltip for collapsed state */}
              {!expanded && (
                <span className="absolute left-full ml-2 top-1/2 -translate-y-1/2 bg-gray-900 text-white text-xs rounded px-2 py-1 opacity-0 group-hover:opacity-100 pointer-events-none whitespace-nowrap z-10">
                  {item.label}
                </span>
              )}
              {/* Notification badge placeholder */}
              {item.key === 'approvals' && (
                <span className="absolute top-1 right-1 bg-red-500 text-white text-[10px] rounded-full px-1.5 py-0.5">3</span>
              )}
            </button>
          ))}
        </nav>
      </div>
    </aside>
  );
}
