import React, { useState } from 'react';
import Button from './Button';
import CustomCheckbox from './CustomCheckbox';
import Badge from './Badge';
import RequestIcon from './RequestIcon';

/**
 * RequestCard component for displaying procurement requests
 * Matches the design shown in the screenshot
 */
export default function RequestCard({
  vendor,
  isNewVendor = false,
  title,
  date,
  requester,
  amount,
  status, // This will be the button text (OPEN, APPROVE)
  onStatusClick,
  selected = false,
  onSelect,
  badge,
  badgeColor = 'green',
  onCardClick,
  className = '',
}) {
  const [hovered, setHovered] = useState(false);

  return (
    <div
      className={`relative p-6 rounded-xl border-2 transition-all duration-150 cursor-pointer
        ${selected ? 'border-blue-500 bg-blue-50' : hovered ? 'border-gray-300 bg-gray-50' : 'border-gray-200 bg-white'}
        ${className}`}
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
      onClick={onCardClick}
    >
      <div className="flex items-center space-x-6">
        {/* Checkbox */}
        <div className="flex items-center justify-center w-8 h-8" onClick={e => e.stopPropagation()}>
          <CustomCheckbox 
            checked={selected} 
            onChange={onSelect}
          />
        </div>

        {/* Request Icon with REQUEST text */}
        <div className="flex flex-col items-center min-w-[64px]">
          <RequestIcon />
          <span className="text-xs text-gray-500 mt-1 font-medium">REQUEST</span>
        </div>

        {/* Vendor and Title */}
        <div className="flex-1 min-w-0">
          <div className="flex items-center space-x-2 mb-1">
            <h3 className="text-lg font-semibold text-gray-900 truncate">{vendor}</h3>
            {isNewVendor && (
              <Badge 
                text="New vendor" 
                color="bg-green-100 text-green-800" 
              />
            )}
            {badge && !isNewVendor && (
              <Badge 
                text={badge} 
                color={`bg-${badgeColor}-100 text-${badgeColor}-800`} 
              />
            )}
          </div>
          <p className="text-gray-700 text-sm font-medium truncate">{title}</p>
        </div>

        {/* Date */}
        <div className="text-right min-w-[120px]">
          <p className="text-xs text-gray-500 mb-1 font-medium">
            {status === 'OPEN' ? 'Created' : 'Requested'}
          </p>
          <p className="text-sm text-gray-700 font-medium">{date}</p>
        </div>

        {/* Amount and Requester */}
        <div className="text-right min-w-[150px]">
          <p className="text-lg font-bold text-gray-900 mb-1">{amount}</p>
          <p className="text-xs text-gray-500 font-medium">Requested by {requester}</p>
        </div>

        {/* Action Button */}
        <div className="min-w-[100px]">
          <Button
            variant="secondary"
            className="px-6 py-2 text-sm font-medium border-2 border-gray-300 text-gray-700 bg-white hover:bg-gray-50 rounded-lg"
            onClick={e => {
              e.stopPropagation();
              onStatusClick();
            }}
          >
            {status}
          </Button>
        </div>
      </div>

      {/* Selection indicator */}
      {selected && (
        <div className="absolute -left-1 top-1/2 transform -translate-y-1/2 w-2 h-12 bg-blue-500 rounded-r"></div>
      )}
    </div>
  );
}
